import { CanvasRenderer } from 'echarts/renderers';
echarts.use([LineChart, TitleComponent, TooltipComponent, GridComponent, DatasetComponent, LegendComponent, DataZoomComponent, ToolboxComponent, CanvasRenderer]);

/* ---------- 工具 ---------- */
function formatDateLabel(v) {
  const d = new Date(v);
  const M = d.getMonth() + 1;
  const D = d.getDate();
  const h = ('0' + d.getHours()).slice(-2);
  const m = ('0' + d.getMinutes()).slice(-2);
  return `${M}-${D} ${h}:${m}`;
}
function buildYAxis(u) {
  return {
    type: 'value',
    name: u,
    axisLabel: {
      formatter: `{value} ${u}`
    },
    max: v => v.max * 1.1,
    minInterval: 1
  };
}
function grad(c) {
  return {
    type: 'linear',
    x: 0,
    y: 0,
    x2: 0,
    y2: 1,
    colorStops: [{
      offset: 0,
      color: c
    }, {
      offset: 1,
      color: `${c}22`
    }]
  };
}
function exportData(format, name, labels, values) {
  const rows = [['时间', name]].concat(labels.map((t, i) => [t, values[i]]));
  let blob, ext;
  if (format === 'csv') {
    const csv = rows.map(r => r.join(',')).join('\r\n');
    blob = new Blob([csv], {
      type: 'text/csv;charset=utf-8'
    });
    ext = '.csv';
  } else {
    const xmlRows = rows.map(r => `<Row>${r.map(c => `<Cell><Data ss:Type="String">${c}</Data></Cell>`).join('')}</Row>`).join('');
    const xml = `<?xml version="1.0"?><Workbook xmlns="urn:schemas-microsoft-com:office:spreadsheet"
                 xmlns:ss="urn:schemas-microsoft-com:office:spreadsheet"><Worksheet ss:Name="${name}">
                 <Table>${xmlRows}</Table></Worksheet></Workbook>`;
    blob = new Blob([xml], {
      type: 'application/vnd.ms-excel'
    });
    ext = '.xlsx';
  }
  const url = URL.createObjectURL(blob);
  const a = document.createElement('a');
  a.href = url;
  a.download = name + ext;
  a.click();
  URL.revokeObjectURL(url);
}
/* ---------- /工具 ---------- */

export default {
  name: 'GreenhouseDetail',
  components: {
    VChart
  },
  data() {
    return {
      greenhouse: {
        irrigationStatus: 0,
        ventilationStatus: 0,
        lightingStatus: 0
      },
      temperatureChart: {},
      humidityChart: {},
      soilChart: {},
      lightChart: {}
    };
  },
  created() {
    const id = this.$route.query.id;
    this.loadGreenhouse(id);
    this.loadEnvData(id);
  },
  methods: {
    /* 设备开关 */
    toggle(type) {
      const map = {
        irrigation: '灌溉',
        ventilation: '通风',
        lighting: '灯光'
      };
      const id = this.$route.query.id;
      this.$request.post(`/api/greenhouse/${id}/toggle/${type}`).then(() => {
        this.$message.success(`${map[type]}状态已切换`);
        const key = `${type}Status`;
        this.greenhouse[key] = this.greenhouse[key] ? 0 : 1;
      });
    },
    handleVideoError() {
      this.$message.error('无信号，请检查摄像设备');
    },
    loadGreenhouse(id) {
      this.$request.get(`/api/greenhouse/getById?id=${id}`).then(r => {
        this.greenhouse = r.data || {};
        if (this.greenhouse.videoUrl && !/^https?:/.test(this.greenhouse.videoUrl)) this.greenhouse.videoUrl = this.$baseUrl + this.greenhouse.videoUrl;
      });
    },
    mkToolbox(seriesName, labels, dataArr) {
      return {
        right: 20,
        feature: {
          saveAsImage: {},
          myCSV: {
            title: '下载 CSV',
            icon: 'path://M896 704H640v-64h256V128H128v512h256v64H128c-35.296 0-64-28.704-64-64V128c0-35.296 28.704-64 64-64h768c35.296 0 64 28.704 64 64v512c0 35.296-28.704 64-64 64z M704 768l128 128 128-128h-96V448h-64v320H704z',
            onclick: () => exportData('csv', seriesName, labels, dataArr)
          },
          myXLSX: {
            title: '下载 XLSX',
            icon: 'path://M704 768l128 128 128-128h-96V448h-64v320H704z M896 704H640v-64h256V128H128v512h256v64H128c-35.296 0-64-28.704-64-64V128c0-35.296 28.704-64 64-64h768c35.296 0 64 28.704 64 64v512c0 35.296-28.704 64-64 64z',
            onclick: () => exportData('xlsx', seriesName, labels, dataArr)
          }
        }
      };
    },
    loadEnvData(id) {
      this.$request.get(`/api/environment/history/${id}`).then(res => {
        const rows = res.data || [];
        const dates = rows.map(r => r.recordDate);
        const xAxis = {
          type: 'category',
          data: dates,
          axisLabel: {
            formatter: formatDateLabel
          }
        };
        const zoom = {
          dataZoom: [{
            type: 'inside',
            xAxisIndex: 0
          }, {
            type: 'slider',
            xAxisIndex: 0
          }]
        };
        const temp = rows.map(r => r.temperature);
        this.temperatureChart = {
          color: [grad('#ff7f50')],
          tooltip: {
            trigger: 'axis'
          },
          xAxis,
          yAxis: buildYAxis('℃'),
          ...zoom,
          toolbox: this.mkToolbox('温度', dates, temp),
          series: [{
            name: '温度',
            type: 'line',
            smooth: true,
            data: temp
          }]
        };
        const hum = rows.map(r => r.humidity);
        this.humidityChart = {
          color: [grad('#1e90ff')],
          tooltip: {
            trigger: 'axis'
          },
          xAxis,
          yAxis: buildYAxis('%'),
          ...zoom,
          toolbox: this.mkToolbox('湿度', dates, hum),
          series: [{
            name: '湿度',
            type: 'line',
            smooth: true,
            data: hum
          }]
        };
        const soil = rows.map(r => r.soilMoisture);
        this.soilChart = {
          color: [grad('#3cb371')],
          tooltip: {
            trigger: 'axis'
          },
          xAxis,
          yAxis: buildYAxis('%'),
          ...zoom,
          toolbox: this.mkToolbox('土壤湿度', dates, soil),
          series: [{
            name: '土壤湿度',
            type: 'line',
            smooth: true,
            data: soil
          }]
        };
        const light = rows.map(r => r.lightIntensity);
        this.lightChart = {
          color: [grad('#ffa500')],
          tooltip: {
            trigger: 'axis'
          },
          xAxis,
          yAxis: buildYAxis('lx'),
          ...zoom,
          toolbox: this.mkToolbox('光照强度', dates, light),
          series: [{
            name: '光照强度',
            type: 'line',
            smooth: true,
            data: light
          }]
        };
      });
    }
  }
